import React, { useState, useEffect } from 'react';

// A simple SVG placeholder, base64 encoded
const PLACEHOLDER_SVG = 'data:image/svg+xml;base64,';

interface ImageWithFallbackProps extends React.ImgHTMLAttributes<HTMLImageElement> {
  fallbackSrc?: string;
}

const ImageWithFallback: React.FC<ImageWithFallbackProps> = ({ src, fallbackSrc = PLACEHOLDER_SVG, ...props }) => {
  const [imgSrc, setImgSrc] = useState(src);

  useEffect(() => {
    setImgSrc(src);
  }, [src]);

  const handleError = () => {
    // Avoids infinite loop if the fallback source is also invalid
    if (imgSrc !== fallbackSrc) {
      setImgSrc(fallbackSrc);
    }
  };

  return <img src={imgSrc || fallbackSrc} onError={handleError} {...props} />;
};

export default ImageWithFallback;
